import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import BootstrapTable from 'react-bootstrap-table-next';
import axios from 'axios';
import { ProgressBar, Card } from 'react-bootstrap';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';

const activity_factors = {
    0: 1.2,
    1: 1.375,
    2: 1.55,
    3: 1.725,
    4: 1.9
}

const multiplier = {
    0: -0.2,
    1: -0.1,
    2: 0,
    3: 0.1,
    4: 0.2
}

class Dashboard extends Component {
  constructor(props) {
    super(props)
    this.state = {
      columns: 
      [{
        dataField: 'food_id',
        hidden: 'true',
        text: 'ID'
      }, {
        dataField: 'name',
        text: 'Name'
      }, {
        dataField: 'servings',
        text: 'Servings (size of 100g)'
      }],
      history: [],
      isLoading: true,
      tdee: this.calcTDEE(props.user),  
      calories: 0,
      fat: 0,
      protein: 0,
      carbs: 0
    }
  }


  calcTDEE = (user) => {
    var bmr = 0
    if (user.gender == 'male') {
      bmr = 66 + (13.7 * user.weight) + (5 * user.height) - (6.8 * user.age)
    }
    else {
      bmr = 655 + (9.6 * user.weight) + (1.8 * user.height) - (4.7 * user.age)
    }

    const tdee = activity_factors[user.activity_level_id] * bmr
    var goal_calories = (1 + multiplier[user.goal_id]) * tdee
    goal_calories = Number((goal_calories).toFixed(1))

    return goal_calories 
  }

  formatDate() {
        var d = new Date(),
              month = '' + (d.getMonth() + 1),
              day = '' + d.getDate(),
              year = d.getFullYear();

        if (month.length < 2) 
              month = '0' + month;
        if (day.length < 2) 
              day = '0' + day;

        return [year, month, day].join('-');
  }

  filterToday(history) {
    const today = this.formatDate()
    var new_history = []
    for (var h of history) {
      if (h.created_at === today)
        new_history.push(h)
    }
    return new_history
  }

  calcMacros(history) {
    var macros = {
      fats: 0,
      carbs: 0,
      protein: 0,
      calories: 0
    }

    for (var h of history) {
      macros.fats += parseFloat(h.fat)
      macros.calories += parseFloat(h.calories)
      macros.carbs += parseFloat(h.carbs)
      macros.protein += parseFloat(h.protein)
    }

    return macros
  }

  getHistory = () => {
    axios.get('/api/food_histories')
      .then(({ data }) => {
        var td_history = this.filterToday(data.food_history)
        var macros = this.calcMacros(td_history)

        this.setState({
          history: td_history,
          carbs: macros.carbs,
          fats: macros.fats,
          calories: macros.calories,
          protein: macros.protein,
        });
      })
      .catch(error => console.log('api errors:', error))
  }

  componentDidMount() {
    this.getHistory()
  }

  render() {
    const { checkedLogin, isLoggedIn, user } = this.props;
    if (!checkedLogin) {
      return null;
    }

    return (
      <div className="jumbotron jumbotron-fluid bg-transparent">
        <div className="container secondary-color">
          <h1 className="display-4">Welcome, {user.first_name}!</h1>
          <p className="lead">
            Get started by <a href='/pick_food'> adding your meals </a> for the day!
          </p>
          <hr className="my-4" />
          <h2>Today's Meals 🍞 🍇</h2>
          <BootstrapTable 
            bootstrap4
            hover
            striped
            keyField="food_id" data={ this.state.history } columns={ this.state.columns } /> 

          <Link to="/pick_food" className="btn btn-lg custom-button mr-2" role="button">Add Food</Link>
          <hr className="my-4" />

          <h2>Meeting Your Goals 💪</h2>
          <Card>
            <Card.Header>Progress</Card.Header>
            <Card.Body>
              <Card.Text>
                Caloric goal: {this.state.tdee} 
              </Card.Text>

            <ProgressBar now={100 * this.state.calories / this.state.tdee} />

            </Card.Body>
          </Card>
        </div>
      </div>
    );
  }
}

export default Dashboard;
