import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import BootstrapTable from 'react-bootstrap-table-next';
import axios from 'axios';
import { ProgressBar, Card } from 'react-bootstrap';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';

const activity_factors = {
    0: 1.2,
    1: 1.375,
    2: 1.55,
    3: 1.725,
    4: 1.9
}

const multiplier = {
    0: -0.2,
    1: -0.1,
    2: 0,
    3: 0.1,
    4: 0.2
}

class Dashboard extends Component {
  constructor(props) {
    super(props)
    this.state = {
      columns: 
      [{
        dataField: 'food_id',
        hidden: 'true',
        text: 'ID'
      }, {
        dataField: 'name',
        text: 'Name'
      }, {
        dataField: 'servings',
        text: 'Amount (in g)'
      }],
      history: [],
      isLoading: true,
      progress: 0,
      tdee: 0,
      calories: 0
    }
  }

  getHistory = () => {
    axios.get('/api/daily_analytics')
      .then(({ data }) => {
        console.log(data)
        this.setState({
          history: data.daily_history,
          progress: parseInt(100 * data.progress[0].progress),
          tdee: data.tdee,
          calories: data.calories
        });
      })
      .catch(error => console.log('api errors:', error))
  }

  componentDidMount() {
    this.getHistory()
  }

  render() {
    const { checkedLogin, isLoggedIn, user } = this.props;
    if (!checkedLogin) {
      return null;
    }

    return (
      <div className="jumbotron jumbotron-fluid bg-transparent">
        <div className="container secondary-color">
          <h1 className="display-4">Welcome!</h1>
          <p className="lead">
          {user.email}, get started by adding your meals for the day!
          </p>
          <hr className="my-4" />
          <h3 className="display-4">Today</h3>
          <BootstrapTable 
            bootstrap4
            hover
            striped
            keyField="food_id" data={ this.state.history } columns={ this.state.columns } /> 

          <Card>
            <Card.Header>Progress</Card.Header>
            <Card.Body>
              <Card.Text>
                Caloric goal: {this.state.tdee} 
              </Card.Text>

            <ProgressBar now={this.state.progress} label={`${this.state.progress}%`} />

            </Card.Body>
          </Card>

          <Link to="/pick_food" className="btn btn-lg custom-button mr-2" role="button">Add Food</Link>
        </div>
      </div>
    );
  }
}

export default Dashboard;
